import React from "react";
import { ClipboardStatic } from "react-native";
import { Box, SimpleGrid, Text, Icon, Pressable, useToast } from "native-base";
import styled, { css } from "styled-components/native";
import Clipboard from "expo-clipboard";

const IconContent = styled(Box)`
  border-bottom-width: 1px;
  border-bottom-color: #ccc;
  align-items: center;
  justify-content: center;
  width: 100px;
  height: 70px;
`;
const IconWrapper = styled(Pressable)`
  border: 1px solid #ccc;
  border-radius: 5px;
  width: 100px;
  height: 100px;

`;
const IconListWrapper = styled.View`
flex: 1;
${IconContent} {
    ${({color, size}) => css`
      color: ${color};
      width: ${size}px;
      height: ${size}px;
    `}
  }
`;

const IconLabel = styled(Text)`
  align-items: center;
  justify-content: center;
  padding: 2px;
  width: 100px;
  height: 30px;
  font-size: 9px;
  text-align: center;
`;

export default function IconList({
  maxCount,
  allVariantNames = [],
  color = "#ccc",
  size = 24,
  variant = "regular",
  allIconNames = [],
  component: Iconset,
  children,
  onIconPress,
  numColumn = 3,
}) {

  const toast = useToast();

  if (!Iconset) {
    return null;
  }

  return (
    <>
      <SimpleGrid
        alignItems="center"
        columns={numColumn}
        spacingY={4}
        spacingX={4}
      >
        <IconListWrapper color={color} size={size}>
        {allIconNames &&
          allIconNames.slice(0, maxCount).map((icon) => (
            <IconWrapper onPress={() => onIconPress && onIconPress(icon)} key={icon}>
              <IconContent>
                <Icon as={Iconset} variant={variant} name={icon}  />
              </IconContent>
              <IconLabel noOfLines={3} numberOfLines={3}>
                {icon}
              </IconLabel>
            </IconWrapper>
          ))}
          </IconListWrapper>
      </SimpleGrid>
      {children}
    </>
  );
}
